def find_words_beginwith(input_string, begin_char):        all_word_array = []    result_word_array = []    word = ""    for char in input_string:                if char != ' ':            word += char        else:            all_word_array.append(word)            word = ""                if word != "":        all_word_array.append(word)                        for i in range(0,len(all_word_array)):                if all_word_array[i][0] == begin_char:           result_word_array.append(all_word_array[i])        else:                  pass            return result_word_array        input_str = "Muir was noted for being an ecological thinker, political spokesman, and religious prophet, whose writings became a personal guide intonature for many people, making his name \"almost ubiquitous\" in the modern environmental consciousness."#Positive test cases#testcase_1#print(find_words_beginwith(input_str,"w"))#testcase_2#print(find_words_beginwith(input_str,"a"))#testcase_3#print(find_words_beginwith(input_str,"t"))#testcase_4#print(find_words_beginwith(input_str,"6"))#Negative test cases#testcase_5(input without quotation)#print(find_words_beginwith(input_str,a))#testcase_6(input string contains double space)input_str = "I  like coding"print(find_words_beginwith(input_str,"c"))